import React, { useState } from 'react';
import { Form, Button, Container } from 'react-bootstrap';

function CreateProduct() {
    const [product, setProduct] = useState({
        name: '',
        description: '',
        price: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setProduct({
            ...product,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Aquí puedes agregar la lógica para enviar los datos del producto, como un POST a una API
        console.log('Producto creado:', product);
    };

    return (
        <Container >
            <h2 className="mt-4">Crear Producto</h2>
            <Form onSubmit={handleSubmit}>
                div
                <Form.Group className="mb-3" controlId="formProductName">
                    <Form.Label>Nombre del Producto</Form.Label>
                    <Form.Control
                        type="text"
                        placeholder="Ingresa el nombre del producto"
                        name="name"
                        value={product.name}
                        onChange={handleChange}
                    />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formProductDescription">
                    <Form.Label>Descripción</Form.Label>
                    <Form.Control as="textarea"  rows={1} placeholder="Ingresa una descripción del producto"
                        name="description"
                        value={product.description}
                        onChange={handleChange}
                    />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formProductPrice">
                    <Form.Label>Precio</Form.Label>
                    <Form.Control
                        type="number"
                        placeholder="Ingresa el precio"
                        name="price"
                        value={product.price}
                        onChange={handleChange}
                    />
                </Form.Group>

                <Button variant="primary" type="submit">
                    Crear Producto
                </Button>
            </Form>
        </Container>
    );
}

export default CreateProduct;
