import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Table, Container, Button, Modal, Form } from 'react-bootstrap';

const ProductList = () => {
  const [products, setProducts] = useState([]);

  // Usamos useEffect para hacer la solicitud al backend cuando el componente se monta
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/products');
        setProducts(response.data);  // Guardamos los productos en el estado
      } catch (error) {
        console.error('Error al obtener los productos:', error);
      }
    };

    fetchProducts();
  }, []);  // El array vacío asegura que solo se ejecute una vez al montar el componente

   // Función para eliminar un producto
   const deleteProduct = async (productId) => {
    try {
      await axios.delete(`http://localhost:5000/api/products/${productId}`);
      alert('Producto eliminado correctamente');
      setProducts(products.filter(product => product.id !== productId));
    } catch (error) {
      console.error('Error al eliminar el producto:', error);
      alert('Hubo un error al eliminar el producto');
    }
  };

  // Función para editar un producto
  const editProduct = async (productId) => {
    try {
      await axios.put(`http://localhost:5000/api/products/${productId}`);
      alert('Producto editado correctamente');
      setProducts(products.filter(product => product.id !== productId));
    } catch (error) {
      console.error('Error al editar el producto:', error);
      alert('Hubo un error al editar el producto');
    }
  }
  

  return (
    <Container className="mt-4">
      <h2>Listado de Productos</h2>
      <Table striped bordered hover responsive>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre</th>
            <th>Descripción</th>
            <th>Precio</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {products.length > 0 ? (
            products.map((product) => (
              <tr key={product.id}>
                <td>{product.id_product}</td>
                <td>{product.name_product}</td>
                <td>{product.description_product}</td>
                <td>{product.price_product}</td>
                <td>
                <Button style={{ backgroundColor: 'orange', borderColor: 'orange' }}  variant="danger" onClick={() => editProduct(product.id)}>
                Editar
              </Button>
                <Button style={{ backgroundColor: 'red', borderColor: 'red' }}  variant="danger" onClick={() => deleteProduct(product.id)}>
                Eliminar
              </Button>
                </td>
              
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4" className="text-center">
                No hay productos disponibles.
              </td>
            </tr>
          )}
        </tbody>
      </Table>
    </Container>
  );
};

export default ProductList;
