import React, { useState } from 'react';
import { Form, Button, Container } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

function CreateProduct() {
    const [product, setProduct] = useState({
        name: '',
        description: '',
        price: ''
    });

    const handleChange = (e) => {
        setProduct({
            ...product,
            [e.target.name]: e.target.value
        });
    };
    const navigate = useNavigate();

const handleVolver = () => {
    navigate(-1);  // Esto vuelve a la página anterior en el historial
};

    const handleSubmit = (e) => {
        e.preventDefault();
        // Aquí iría tu lógica para enviar el formulario
    };

    return (
        <Container >
            <h2 className="form-product-title">Crear Producto</h2>
            <Form onSubmit={handleSubmit} >
                <Form.Group className="form-product-group" controlId="formProductName">
                    <Form.Label>Nombre del Producto</Form.Label>
                    <Form.Control
                        type="text"
                        placeholder="Ingresa el nombre del producto"
                        name="name"
                        value={product.name}
                        onChange={handleChange}
                    />
                </Form.Group>

                <Form.Group className="form-product-group" controlId="formProductDescription">
                    <Form.Label>Descripción</Form.Label>
                    <Form.Control
                        as="textarea"
                        rows={3}
                        placeholder="Ingresa una descripción del producto"
                        name="description"
                        value={product.description}
                        onChange={handleChange}
                    />
                </Form.Group>

                <Form.Group className="form-product-group" controlId="formProductPrice">
                    <Form.Label>Precio</Form.Label>
                    <Form.Control
                        type="number"
                        placeholder="Ingresa el precio"
                        name="price"
                        value={product.price}
                        onChange={handleChange}
                    />
                </Form.Group>

                <Button className="form-submit-button" variant="primary" type="submit">
                    Crear Producto
                </Button>
                <Button className="form-submit-button  " variant="secondary" type="">
                    volver
                </Button>

            </Form>
        </Container>
    );
}

export default CreateProduct;
