import React, { useState } from 'react';
import axios from 'axios';
import { Button, Table, Modal, Form } from 'react-bootstrap';

const ProductList = () => {
    const [products, setProducts] = useState([]); // Lista de productos
    const [showEditModal, setShowEditModal] = useState(false); // Estado para mostrar el modal de edición
    const [selectedProduct, setSelectedProduct] = useState({
        id: '',
        name: '',
        description: '',
        price: ''
    });

    // Función para cargar productos de la base de datos
    const fetchProducts = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/products');
            setProducts(response.data);
        } catch (error) {
            console.error('Error al cargar productos:', error);
        }
    };

    // Llama a la función fetchProducts para cargar productos cuando el componente se monta
    useState(() => {
        fetchProducts();
    }, []);

    // Maneja el cambio de datos en el formulario del modal
    const handleChange = (e) => {
        const { name, value } = e.target;
        setSelectedProduct((prev) => ({ ...prev, [name]: value }));
    };

    // Abre el modal de edición y carga los datos del producto seleccionado
    const handleEditClick = (product) => {
        setSelectedProduct(product);
        setShowEditModal(true);
    };

    // Maneja la actualización del producto
    const handleUpdate = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`http://localhost:5000/api/products/${selectedProduct.id}`, selectedProduct);
            alert('Producto actualizado exitosamente');
            setShowEditModal(false);
            fetchProducts(); // Recargar los productos para reflejar los cambios
        } catch (error) {
            console.error('Error al actualizar el producto:', error);
            alert('Hubo un error al actualizar el producto');
        }
    };

    return (
        <div>
            <h2>Lista de Productos</h2>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nombre</th>
                        <th>Descripción</th>
                        <th>Precio</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map((product) => (
                        <tr key={product.id}>
                            <td>{product.id}</td>
                            <td>{product.name}</td>
                            <td>{product.description}</td>
                            <td>{product.price}</td>
                            <td>
                                <Button variant="warning" onClick={() => handleEditClick(product)}>
                                    Editar
                                </Button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>

            {/* Modal para editar el producto */}
            <Modal show={showEditModal} onHide={() => setShowEditModal(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Editar Producto</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form onSubmit={handleUpdate}>
                        <Form.Group controlId="formProductName">
                            <Form.Label>Nombre del Producto</Form.Label>
                            <Form.Control
                                type="text"
                                placeholder="Nombre del producto"
                                name="name"
                                value={selectedProduct.name}
                                onChange={handleChange}
                            />
                        </Form.Group>
                        <Form.Group controlId="formProductDescription" className="mt-3">
                            <Form.Label>Descripción</Form.Label>
                            <Form.Control
                                as="textarea"
                                rows={2}
                                placeholder="Descripción del producto"
                                name="description"
                                value={selectedProduct.description}
                                onChange={handleChange}
                            />
                        </Form.Group>
                        <Form.Group controlId="formProductPrice" className="mt-3">
                            <Form.Label>Precio</Form.Label>
                            <Form.Control
                                type="number"
                                placeholder="Precio del producto"
                                name="price"
                                value={selectedProduct.price}
                                onChange={handleChange}
                            />
                        </Form.Group>
                        <Button variant="primary" type="submit" className="mt-3">
                            Guardar Cambios
                        </Button>
                    </Form>
                </Modal.Body>
            </Modal>
        </div>
    );
};

export default ProductList;
